import React, {useEffect, useState} from 'react';
import {Container, ContainerSize} from "../Container/Container";
import {useNavigate} from "react-router-dom";
import {useQuery} from '../../utils/hooks';
import {Ingredient, Meal} from "../AddMeal/AddMeal";
import {Spacer, Spacing} from "../Spacer/Spacer";
import defaultImage from '../../images/defaultImage.jpg';

export const MealDetailView = () => {
  let [meal, setMeal] = useState<Meal>();

  let navigate = useNavigate();
  let query = useQuery();

  let mealMock = require('./MockData/meal.json');

  useEffect(() => {
    let mealTitle = query.get("meal");
    console.log(mealTitle);

    // TODO: fetch meal info

    setMeal(mealMock);
  });

  const handleClick = (e : any) => {
    navigate(-1);
  }

  return (<Container size={ContainerSize.Small}>
    <Spacer size={Spacing.m} />
    <p style={{paddingLeft:"20px", alignSelf: "baseline", color:"blue", cursor:"pointer", fontWeight:700}}>‚Üê back</p>
    {meal && <MealView meal={meal} />}
    <Spacer size={Spacing.m} />
  </Container>)
}

interface MealViewProp {
  meal: Meal;
}
const MealView : React.FC<MealViewProp> = ({meal}) => {
  let ingredients = meal.ingredients.map((ingredient : Ingredient) => <li>{ingredient.title}: {ingredient.amount}</li>);

  return (
    <div style={{textAlign:"left", padding:"20px", display:"flex", flexDirection: "column", alignItems:"center"}}>
      <img style={{height: "140px", width: "140px", borderRadius: "12px"}} src={meal.imagePath ? meal.imagePath : defaultImage} />
      <Spacer size={Spacing.xs} />
      <h1>{meal.title}</h1>
      <Spacer size={Spacing.l} />
      <h2>Ingredients</h2>
        <ul>
          {ingredients}
        </ul>
      <Spacer size={Spacing.s} />
        {meal.description && (
          <div style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
            <h2>Recipe</h2>
            <Spacer size={Spacing.xs} />
            <p style={{fontSize: "14px", textAlign:"justify"}}>{meal.description}</p>
          </div>
        )}
    </div>
  )
}
