import * as React from 'react';
import IconButton from '@mui/material/IconButton';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import {useNavigate, useParams} from "react-router-dom";

const ITEM_HEIGHT = 48;

const getYearRange = () : number[] => {
  let yearRange : number[] = [];

  let currentYear : number = new Date().getFullYear();
  let from : number = currentYear - 10;
  let to : number = currentYear + 10;
  for (let i = from; i <= to; i++) {
    yearRange.push(i);
  }

  return yearRange;
}

export default function YearMenu() {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);
  const { year, week } = useParams();
  const navigate = useNavigate();

  const options = getYearRange();

  let yearParsed : number = year ? parseInt(year) : new Date().getFullYear();
  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <p
        onClick={handleClick}
        style={{color:"blue", fontWeight:700}}
      >
        â†’ {year}
      </p>
      <Menu
        id="long-menu"
        MenuListProps={{
          'aria-labelledby': 'long-button',
        }}
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
      >
        {options.map((option) => (
          <MenuItem key={option} selected={option === yearParsed} onClick={handleClose}>
            {option}
          </MenuItem>
        ))}
      </Menu>
    </div>
  );
}
