import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Typography
} from '@mui/material';
import Button from '@mui/material/Button';
import React from 'react';

interface MediaProps {
  imagePath: string | null;
  imageTitle: string;
  actionLabel?: string;
  clickEvent: Function;
}
export const MediaCard: React.FC<MediaProps> = ({
  imagePath,
  imageTitle,
  actionLabel = "Remove",
  clickEvent,
}) => {
  return (
    <Card sx={{ width: 140, height: 140 }}>
      <CardMedia sx={{ height: 80 }} image={imagePath ? imagePath : "/static/media/background.dc079cd787ef11b2cc52.jpg"} title={imageTitle} />
      <CardContent style={{ padding: '0px' }}>
        <p style={{ fontWeight: 700 }}>{imageTitle}</p>
      </CardContent>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <CardActions>
          <Button onClick={() => clickEvent()} size="small">{actionLabel}</Button>
        </CardActions>
      </div>
    </Card>
  );
};
