import React, {ChangeEvent, useRef, useState} from 'react';
import {useNavigate} from "react-router-dom";
import {Container, ContainerSize} from "../Container/Container";
import {Spacer, Spacing} from "../Spacer/Spacer";
import './NewMeal.css';
import defaultImage from '../../images/defaultImage.jpg';

export const NewMeal : React.FC<{}> = () => {
  const [recipeImage, setRecipeImage] = useState<File>();
  const [preview, setPreview] = useState<string>();

  const navigate = useNavigate();
  const fileRef = useRef<HTMLInputElement>(null);
  const handleImageClick = () => {
      fileRef.current?.click();
  }

  const handleImageChang = (e : ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];

    if (file) {
      setRecipeImage(file);
      const objectUrl = URL.createObjectURL(file);
      setPreview(objectUrl);
    }
  }

  return (<Container size={ContainerSize.Big}>
    <Spacer size={Spacing.m} />
      <h1>New Meal</h1>
      <Spacer size={Spacing.s} />
    <div style={{display:"flex", justifyContent:"center", alignItems: "center", flexDirection:"column"}}>
      <div className={"RecipeFormPart"}>
        <h2>General</h2>
        <div>
            <img id="recipeImage" src={preview ? preview : defaultImage} onClick={handleImageClick}/>
            <input type='file' style={{display:"none"}} ref={fileRef} onChange={() => console.log("test")} />
        </div>
      </div>
      <Spacer size={Spacing.s} />
      <div className={"RecipeFormPart"}>
        <h2>Ingredients</h2>
      </div>
      <Spacer size={Spacing.s} />
      <div className={"RecipeFormPart"}>
        <h2>Recipe</h2>
      </div>
      <Spacer size={Spacing.s} />
    </div>
  </Container>)
}
